    public static function getRandomAvatar()
    {
        return self::$avatar[array_rand(self::$avatar)];
    }

    public static function getRandomHashtag()
    {
        return '#' . self::$hashtag[array_rand(self::$hashtag)];
    }

    public static function getRandomDomain()
    {
        return self::getRandomString(6) . '.' . self::getRandomString(2, false);
    }

    public static function getUsername($name)
    {
        return $name . self::getRandomString(4, true);
    }

    public static function getRandomSentence()
    {
        $sentence = '';

        for ($i = 0; $i < rand(3, 8); $i++) {
            $sentence .= self::$words[array_rand(self::$words)] . ' ';
        }

        for ($i = 0; $i < rand(1, 3); $i++) {
            $sentence .= self::getRandomHashtag();
        }

        return $sentence;
    }

    public static function getRandomString($length = 4, $withNumbers = false) {
        if ($withNumbers) {
            $characters = '0123456789abcdefghijklmnopqrstuvwxyz';
        } else {
            $characters = 'abcdefghijklmnopqrstuvwxyz';
        }

        $charactersLength = strlen($characters);
        $randomString = '';

        for ($i = 0; $i < $length; $i++) {
            $randomString .= $characters[rand(0, $charactersLength - 1)];
        }

        return $randomString;
    }

    public static function getRandomDate()
    {
        $min = strtotime('-4 week');
        $max = strtotime('now');

        $val = rand($min, $max);

        return date('Y-m-d H:i:s', $val);
    }
